import 'package:flutter/material.dart';

class AboutScreen extends StatelessWidget {
  const AboutScreen({Key? key}) : super(key: key);

  static const List<Map<String, String>> landmarks = [
    {
      'title': 'Badshahi Mosque (Lahore)',
      'image':
      'data:image/jpeg;base64,'
    },
    {
      'title': 'Faisal Mosque (Islamabad)',
      'image':
      'data:image/jpeg;base64,'
    },
    {
      'title': 'Minar-e-Pakistan (Lahore)',
      'image':
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRmy42IdEQduw52yJ7bBW6mYBYrgf-HbP4OIg&s'
    },
    {
      'title': 'Hunza Valley',
      'image':
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS0eBlveN6voBU7AaExaAop0jdh2TkXl5AOvg&s'
    },
    {
      'title': 'Shandur Polo Ground',
      'image':
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHTNtW63c2t7So5StK3mlckK0z9xxLqcvsZA&s'
    },
    {
      'title': 'K2 View (Skardu region)',
      'image':
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSRP3sx2slWogtTiqPTOXolPVdlht1F7tYFXw&s'
    },
  ];

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.all(8.0),
      child: GridView.builder(
        itemCount: landmarks.length,
        gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
          crossAxisCount: 2,
          crossAxisSpacing: 8,
          mainAxisSpacing: 8,
          childAspectRatio: 0.85,
        ),
        itemBuilder: (context, index) {
          final item = landmarks[index];
          return Card(
            shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.stretch,
              children: [
                Expanded(
                  child: ClipRRect(
                    borderRadius: const BorderRadius.vertical(top: Radius.circular(8)),
                    child: Image.network(
                      item['image']!,
                      fit: BoxFit.cover,
                      loadingBuilder: (context, child, progress) =>
                      progress == null ? child : const Center(child: CircularProgressIndicator()),
                      errorBuilder: (context, error, stackTrace) =>
                          Container(color: Colors.grey[200], child: const Center(child: Icon(Icons.broken_image))),
                    ),
                  ),
                ),
                Padding(
                  padding: const EdgeInsets.all(8.0),
                  child: Text(
                    item['title']!,
                    textAlign: TextAlign.center,
                    style: const TextStyle(fontWeight: FontWeight.bold),
                  ),
                ),
              ],
            ),
          );
        },
      ),
    );
  }
}
